import pandas as pd
import torchvision
from torch.utils.data import Dataset, DataLoader
import torch
from matplotlib import pyplot as plt

# %% ../../nbs/00_data.datasets.ipynb 4
class ImageDataset(Dataset):
    " Base class for image datasets providing visualization of (image, label) samples"

    def show(self,
        index:int # Index of the (image,label) sample to visualize
        ):
        X, y = self.__getitem__(index)
        plt.figure(figsize = (1, 1))
        plt.imshow(X.numpy().reshape(28,28),cmap='gray')
        plt.title(f"Label: {int(y)}")
        plt.show()

# %% ../../nbs/00_data.datasets.ipynb 5
class MNISTDataset(ImageDataset):
    "MNIST digit dataset"

    def __init__(
        self,
        data_root:str='~/Data', # data save path
        train=True, # download train(T)/dev(F)
        transform=torchvision.transforms.ToTensor() # data formatting
    ):

        super().__init__()
        # list(PIL image 28x28, label)
        self.ds = torchvision.datasets.MNIST(
            data_root,
            train = train,
            transform=transform, 
            download=True
        )
    def __len__(self):
        return len(self.ds)
    
    def __getitem__(self, idx):
        # x = self.ds[idx][0].squeeze(0).flatten()
        x = self.ds.data[idx]
        return x, self.ds.targets[idx]
